class Task{constructor(t){this.content=t,this.isCompleted=!1}}class ToDoList{constructor(){this.tasks=JSON.parse(localStorage.getItem("tasks"))||[],this.completed=[],this.init()}addTask(t){if(""!==t)return this.tasks.push(new Task(t)),this.addToLocalStorage(),void this.renderList(this.tasks)}renderList(t){const s=document.getElementById("todo-items");t.forEach(t=>{console.log(t);const e=document.createElement("li");e.innerHTML=t.content,s.append(e)})}addToLocalStorage(t){localStorage.setItem("tasks",JSON.stringify(this.tasks))}bind(){const t=document.getElementById("todo-button-add"),s=document.getElementById("todo-input");t.addEventListener("click",()=>{this.addTask(s.value),s.value=""})}render(t){this.renderList(t)}init(){this.render(this.tasks),this.bind()}}const toDoList=new ToDoList;